import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const VoiceInterface = ({ onTranscript, onResponse }) => {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [error, setError] = useState(null);
  const recognitionRef = useRef(null);

  useEffect(() => {
    // Check if browser supports speech recognition
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      setError('Speech recognition not supported in this browser. Please use Chrome.');
      return;
    }

    // Initialize speech recognition
    const recognition = new SpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = 'en-US';

    recognition.onstart = () => {
      console.log('üé§ Voice recognition started');
      setIsListening(true);
      setError(null);
    };

    recognition.onend = () => {
      console.log('üé§ Voice recognition ended');
      setIsListening(false);
    };

    recognition.onerror = (event) => {
      console.error('Speech recognition error:', event.error);
      setError(`Error: ${event.error}`);
      setIsListening(false);
    };

    recognition.onresult = async (event) => {
      let finalTranscript = '';
      let interimTranscript = '';

      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript;
        if (event.results[i].isFinal) {
          finalTranscript += transcript + ' ';
        } else {
          interimTranscript += transcript;
        }
      }

      // Update display
      setTranscript(interimTranscript || finalTranscript);

      // If we have a final transcript, send it
      if (finalTranscript) {
        console.log('üìù Final transcript:', finalTranscript);

        // Send to parent component
        if (onTranscript) {
          onTranscript(finalTranscript.trim());
        }

        // Optional: Convert response to speech using ElevenLabs
        // We'll add this after testing text works

        setTranscript(''); // Clear for next input
      }
    };

    recognitionRef.current = recognition;

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
    };
  }, [onTranscript]);

  const startListening = () => {
    if (recognitionRef.current && !isListening) {
      try {
        recognitionRef.current.start();
      } catch (error) {
        console.error('Error starting recognition:', error);
        setError('Failed to start microphone');
      }
    }
  };

  const stopListening = () => {
    if (recognitionRef.current && isListening) {
      recognitionRef.current.stop();
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h3 style={styles.title}>üé§ Voice Interface</h3>
        <div style={styles.status}>
          <span style={{
            ...styles.statusDot,
            backgroundColor: isListening ? '#10b981' : '#ef4444'
          }}></span>
          <span style={styles.statusText}>
            {isListening ? 'Listening...' : 'Not listening'}
          </span>
        </div>
      </div>

      <div style={styles.controls}>
        {!isListening ? (
          <button
            onClick={startListening}
            style={{...styles.button, ...styles.startButton}}
          >
            üé§ Start Listening
          </button>
        ) : (
          <button
            onClick={stopListening}
            style={{...styles.button, ...styles.endButton}}
          >
            ‚èπÔ∏è Stop Listening
          </button>
        )}
      </div>

      {error && (
        <div style={styles.error}>
          ‚ö†Ô∏è {error}
        </div>
      )}

      {transcript && (
        <div style={styles.transcript}>
          <strong>Listening:</strong> {transcript}
        </div>
      )}

      <div style={styles.info}>
        <p style={styles.infoText}>
          {isListening 
            ? 'üéôÔ∏è Microphone is active. Try saying: "Generate a function to add two numbers"'
            : 'Click "Start Listening" to begin voice input'}
        </p>
        <p style={styles.helpText}>
          üí° <strong>Voice commands:</strong><br/>
          ‚Ä¢ "Generate..." or "Create..." - Generate code<br/>
          ‚Ä¢ "Debug..." or "Fix..." - Debug current code<br/>
          ‚Ä¢ "Explain..." - Explain current code
        </p>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    backgroundColor: '#1e1e1e',
    borderRadius: '8px',
    color: '#fff',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '20px',
  },
  title: {
    margin: 0,
    fontSize: '20px',
    fontWeight: '600',
  },
  status: {
    display: 'flex',
    alignItems: 'center',
    gap: '8px',
  },
  statusDot: {
    width: '12px',
    height: '12px',
    borderRadius: '50%',
    animation: 'pulse 2s infinite',
  },
  statusText: {
    fontSize: '14px',
    fontWeight: '500',
  },
  controls: {
    marginBottom: '20px',
  },
  button: {
    padding: '14px 28px',
    fontSize: '16px',
    fontWeight: '600',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    transition: 'all 0.3s',
    width: '100%',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.3)',
  },
  startButton: {
    backgroundColor: '#10b981',
    color: '#fff',
  },
  endButton: {
    backgroundColor: '#ef4444',
    color: '#fff',
  },
  error: {
    padding: '12px',
    backgroundColor: '#7f1d1d',
    borderRadius: '6px',
    marginBottom: '16px',
    fontSize: '14px',
  },
  transcript: {
    padding: '12px',
    backgroundColor: '#1f2937',
    borderRadius: '6px',
    marginBottom: '16px',
    fontSize: '14px',
    fontStyle: 'italic',
  },
  info: {
    padding: '16px',
    backgroundColor: '#2d2d2d',
    borderRadius: '6px',
  },
  infoText: {
    margin: '0 0 12px 0',
    fontSize: '14px',
    color: '#a0a0a0',
  },
  helpText: {
    margin: 0,
    fontSize: '13px',
    color: '#9ca3af',
    lineHeight: '1.6',
  },
};

export default VoiceInterface;
