import React, { useState, useEffect } from 'react';

const VoiceInterface = ({ onTranscript }) => {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [recognition, setRecognition] = useState(null);
  const [error, setError] = useState(null);
  const [supported, setSupported] = useState(true);

  useEffect(() => {
    // Check browser support
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      setSupported(false);
      setError('Speech recognition not supported. Please use Chrome browser.');
      return;
    }

    const recognitionInstance = new SpeechRecognition();
    recognitionInstance.continuous = false; // Changed to false for better stability
    recognitionInstance.interimResults = true;
    recognitionInstance.lang = 'en-US';
    recognitionInstance.maxAlternatives = 1;

    recognitionInstance.onstart = () => {
      console.log('üé§ Started listening');
      setIsListening(true);
      setError(null);
    };

    recognitionInstance.onresult = (event) => {
      let interimTranscript = '';
      let finalTranscript = '';

      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcriptPiece = event.results[i][0].transcript;
        if (event.results[i].isFinal) {
          finalTranscript += transcriptPiece + ' ';
        } else {
          interimTranscript += transcriptPiece;
        }
      }

      setTranscript(interimTranscript || finalTranscript);

      // Send final transcript to parent
      if (finalTranscript.trim()) {
        console.log('‚úÖ Final:', finalTranscript.trim());
        onTranscript(finalTranscript.trim());
        setTimeout(() => setTranscript(''), 500);
      }
    };

    recognitionInstance.onerror = (event) => {
      console.error('Speech error:', event.error);

      let errorMessage = '';
      switch(event.error) {
        case 'not-allowed':
        case 'service-not-allowed':
          errorMessage = 'Microphone access denied. Please allow microphone in browser settings.';
          break;
        case 'network':
          errorMessage = 'Network error. Make sure you have internet connection.';
          break;
        case 'no-speech':
          errorMessage = 'No speech detected. Please try again.';
          // Auto-restart
          setTimeout(() => {
            if (isListening) {
              recognitionInstance.start();
            }
          }, 500);
          return;
        case 'aborted':
          errorMessage = 'Speech recognition aborted.';
          break;
        default:
          errorMessage = `Error: ${event.error}`;
      }

      setError(errorMessage);
      setIsListening(false);
    };

    recognitionInstance.onend = () => {
      console.log('üî¥ Recognition ended');
      setIsListening(false);
    };

    setRecognition(recognitionInstance);

    return () => {
      if (recognitionInstance) {
        try {
          recognitionInstance.stop();
        } catch (e) {
          // Ignore errors on cleanup
        }
      }
    };
  }, [onTranscript, isListening]);

  const startListening = () => {
    if (!recognition || isListening) return;

    try {
      setError(null);
      recognition.start();
      console.log('Starting recognition...');
    } catch (err) {
      console.error('Start error:', err);
      setError('Failed to start. Try clicking again.');
      setIsListening(false);
    }
  };

  const stopListening = () => {
    if (!recognition || !isListening) return;

    try {
      recognition.stop();
    } catch (err) {
      console.error('Stop error:', err);
    }
  };

  if (!supported) {
    return (
      <div style={styles.container}>
        <div style={styles.error}>
          ‚ö†Ô∏è Voice input not supported. Please use Google Chrome browser.
        </div>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h3 style={styles.title}>üé§ Voice Input</h3>
        <div style={styles.status}>
          <span style={{
            ...styles.statusDot,
            backgroundColor: isListening ? '#10b981' : '#6b7280'
          }}></span>
          <span>{isListening ? 'Listening' : 'Ready'}</span>
        </div>
      </div>

      {!isListening ? (
        <button
          onClick={startListening}
          style={{...styles.button, backgroundColor: '#10b981'}}
        >
          üé§ Start Voice Input
        </button>
      ) : (
        <button
          onClick={stopListening}
          style={{...styles.button, backgroundColor: '#ef4444'}}
        >
          ‚èπÔ∏è Stop Listening
        </button>
      )}

      {error && (
        <div style={styles.error}>
          ‚ö†Ô∏è {error}
        </div>
      )}

      {transcript && (
        <div style={styles.transcript}>
          <strong>You're saying:</strong> {transcript}
        </div>
      )}

      <div style={styles.info}>
        <p style={{margin: '0 0 8px 0', fontSize: '14px', color: '#9ca3af'}}>
          {isListening 
            ? 'üéôÔ∏è Speak now! Say your command and stop when done.'
            : 'Click Start, speak your command, then click Stop.'}
        </p>
        <p style={{margin: 0, fontSize: '12px', color: '#6b7280'}}>
          <strong>Try:</strong> "Generate a function to add two numbers"
        </p>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    backgroundColor: '#1e1e1e',
    borderRadius: '8px',
    color: '#fff',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '16px',
  },
  title: {
    margin: 0,
    fontSize: '20px',
  },
  status: {
    display: 'flex',
    alignItems: 'center',
    gap: '8px',
    fontSize: '14px',
  },
  statusDot: {
    width: '10px',
    height: '10px',
    borderRadius: '50%',
  },
  button: {
    width: '100%',
    padding: '16px',
    fontSize: '16px',
    fontWeight: '600',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    color: '#fff',
    marginBottom: '16px',
  },
  error: {
    padding: '12px',
    backgroundColor: '#7f1d1d',
    borderRadius: '6px',
    marginBottom: '16px',
    fontSize: '14px',
  },
  transcript: {
    padding: '12px',
    backgroundColor: '#374151',
    borderRadius: '6px',
    marginBottom: '16px',
    fontSize: '14px',
  },
  info: {
    padding: '12px',
    backgroundColor: '#1f2937',
    borderRadius: '6px',
  },
};

export default VoiceInterface;
