import React, { useState } from 'react';
import { useConversation } from '@elevenlabs/react';

const VoiceInterface = ({ onTranscript, onResponse, agentId }) => {
  const [isConnected, setIsConnected] = useState(false);

  const conversation = useConversation({
    onConnect: () => {
      console.log('Connected to ElevenLabs');
      setIsConnected(true);
    },
    onDisconnect: () => {
      console.log('Disconnected from ElevenLabs');
      setIsConnected(false);
    },
    onMessage: (message) => {
      console.log('Message received:', message);
      if (message.type === 'user_transcript') {
        onTranscript && onTranscript(message.text);
      } else if (message.type === 'agent_response') {
        onResponse && onResponse(message.text);
      }
    },
    onError: (error) => {
      console.error('ElevenLabs error:', error);
    },
  });

  const startConversation = async () => {
    try {
      await conversation.startSession({
        agentId: agentId || process.env.REACT_APP_ELEVENLABS_AGENT_ID,
      });
    } catch (error) {
      console.error('Failed to start conversation:', error);
    }
  };

  const endConversation = async () => {
    try {
      await conversation.endSession();
    } catch (error) {
      console.error('Failed to end conversation:', error);
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h3 style={styles.title}>üé§ Voice Interface</h3>
        <div style={styles.status}>
          <span style={{
            ...styles.statusDot,
            backgroundColor: isConnected ? '#10b981' : '#ef4444'
          }}></span>
          <span style={styles.statusText}>
            {isConnected ? 'Connected' : 'Disconnected'}
          </span>
        </div>
      </div>

      <div style={styles.controls}>
        {!isConnected ? (
          <button
            onClick={startConversation}
            style={{...styles.button, ...styles.startButton}}
          >
            Start Conversation
          </button>
        ) : (
          <button
            onClick={endConversation}
            style={{...styles.button, ...styles.endButton}}
          >
            End Conversation
          </button>
        )}
      </div>

      <div style={styles.info}>
        <p style={styles.infoText}>
          {isConnected 
            ? 'üéôÔ∏è Speak now to interact with your AI pair programming assistant!'
            : 'Click "Start Conversation" to begin voice interaction'}
        </p>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    backgroundColor: '#1e1e1e',
    borderRadius: '8px',
    color: '#fff',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '20px',
  },
  title: {
    margin: 0,
    fontSize: '20px',
    fontWeight: '600',
  },
  status: {
    display: 'flex',
    alignItems: 'center',
    gap: '8px',
  },
  statusDot: {
    width: '10px',
    height: '10px',
    borderRadius: '50%',
  },
  statusText: {
    fontSize: '14px',
  },
  controls: {
    marginBottom: '20px',
  },
  button: {
    padding: '12px 24px',
    fontSize: '16px',
    fontWeight: '600',
    border: 'none',
    borderRadius: '6px',
    cursor: 'pointer',
    transition: 'all 0.2s',
  },
  startButton: {
    backgroundColor: '#10b981',
    color: '#fff',
  },
  endButton: {
    backgroundColor: '#ef4444',
    color: '#fff',
  },
  info: {
    padding: '16px',
    backgroundColor: '#2d2d2d',
    borderRadius: '6px',
  },
  infoText: {
    margin: 0,
    fontSize: '14px',
    color: '#a0a0a0',
  },
};

export default VoiceInterface;
